<?php

/**
 * Test class for Validate_Url.
 * Generated by PHPUnit on 2012-06-02 at 11:56:11.
 */
class Validate_UrlTest extends PHPUnit_Framework_TestCase
{

  /**
   * test Validate_Url::isValid
   */
  public function testValid() {
    $validator = new Validate_Url();
    $url = "http://example.com";
    $valid = $validator->isValid($url);
    $this->assertTrue($valid);
  }

  /**
   * test Validate_Url::isValid
   */
  public function testFailedValidation() {
    $validator = new Validate_Url();
    $url = "bad url";
    $valid = $validator->isValid($url);
    $this->assertFalse($valid);
  }

}

